import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
    );
}


class Board extends React.Component {
    renderSquare( row, column ) {
        console.log("rendering the value " + row + ", " + column );
        return (<Square value={this.props.squares[row][column]} onClick = {() => this.props.onClick(row * this.props.boardSize + column)} />);
    }

    renderRow( rowNumber ) {
        var items = [];
        for( var column = 0; column < this.props.boardSize; column++ ) {
            items.push( this.renderSquare( rowNumber, column ) );
        }
        return (
            <div className='board-row'>
                {items}
            </div>
        );
    }

    render() {
        var rows = [];
        for( var i = 0; i < this.props.boardSize; i++ ) {
            rows.push( this.renderRow( i ) );
        }
        return (
            <div>{rows}</div>
        );
  }
}

class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        boardSize: 3,
        squares: Array( 3 ).fill( Array( 3 ).fill( null ) ),
        xIsNext: true,
    };
  }
