import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
    );
}


class Board extends React.Component {
    constructor( props ) {
        super( props );
        this.state = {
            boardSize: 3,
        }
    }
    renderSquare( row, column ) {
        console.log("rendering square at " + row + ", " + column )
        return (<Square value={this.props.squares[row][column]} onClick = {() => this.props.onClick(row,column)} />);
    }

    renderRow( rowNumber ) {
        var items = [];
        for( var column = 0; column < this.state.boardSize; column++ ) {
            items.push( this.renderSquare( rowNumber, column ) );
        }
        return (
            <div className='board-row'>
                {items}
            </div>
        );
    }

    render() {
        var rows = [];
        for( var i = 0; i < this.state.boardSize; i++ ) {
            rows.push( this.renderRow( i ) );
        }
        return (
            <div>{rows}</div>
        );
  }
}

class Game extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        boardSize: 3,
        history: [{
            squares: Array(3).fill(Array(3).fill(null)),
        }],
        xIsNext: true,
    };
  }
  handleClick( row, column ){
    const history = this.state.history;
      console.log(history )
    const current = history[ history.length - 1 ];
    const squares = current.squares;
      console.log( squares );
      console.log("I am clicking on square " + row + ", " + column );
    if ( calculateWinner(squares) || squares[ row ][ column ] ){
        return;
      }
      squares[ row ][ column ] = this.state.xIsNext ? 'X' : 'O';
      console.log( squares );
      history.push( {squares: squares} );
      this.setState({
          history: history,
          xIsNext: !this.state.xIsNext,
    });
    }

  render() {
    const history = this.state.history;
    const current = history[history.length-1];
    const winner = calculateWinner(current.squares);
    let status;
    if (winner){
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
    }
    return (
      <div className="game">
        <div className="game-board">
          <Board 
          squares = {current.squares}
          onClick={(row, column) => this.handleClick( row, column )}
           />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

